mapper.select:
SELECT

from tb_user
where username = #{username}
and password = #{password}

select 	count(1)
from 	tb_user
where 	username = #{username}
and		role = #{role}

select count(1) from tb_user
where email = #{email}
and id != #{userId}

select 	QUESTION
FROM 	TB_USER
WHERE 	USERNAME = #{username}

SELECT
count(1)
from tb_user
where username=#{username}
and question = #{question}
and answer = #{answer}

